<?php class RequestProtection{	Private $previous_hash;	public $hashcode;	function __construct(){		if(isset($_SESSION['bg_request_token'])){			$this->previous_hash=$_SESSION['bg_request_token'];			$_SESSION['bg_request_token']=md5(uniqid());		}			$this->hashcode=$_SESSION['bg_request_token']=md5(uniqid());	}	public function is_valid($key='csrf_token'){		//return $this->previous_hash;exit();		return ((isset($_POST[$key])&&($_POST[$key]==$this->previous_hash))||				(isset($_GET[$key])&&($_GET[$key]==$this->previous_hash)));	}	public function meta_tag($key='csrf_token'){		return '<meta name="'.$key.'"value="'.$this->hashcode .'"/>';	}}